import React, { useState } from 'react';
import BookDetails from './components/BookDetails';
import BlogDetails from './components/BlogDetails';
import CourseDetails from './components/CourseDetails';

function App() {
  const [selectedComponent, setSelectedComponent] = useState('book');

  const renderComponent = () => {
    // 1. If-else rendering
    if (selectedComponent === 'book') return <BookDetails />;
    else if (selectedComponent === 'blog') return <BlogDetails />;
    else if (selectedComponent === 'course') return <CourseDetails />;
    else return <h3>Please select an option</h3>;
  };

  // 2. Element variable
  let element;
  if (selectedComponent === 'blog') {
    element = <BlogDetails />;
  }

  // 3. Ternary operator
  const courseOrBook = selectedComponent === 'course' ? <CourseDetails /> : <BookDetails />;

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial' }}>
      <h2>ðŸ“š Blogger App - Conditional Rendering Demo</h2>

      <button onClick={() => setSelectedComponent('book')}>Show Book</button>
      <button onClick={() => setSelectedComponent('blog')}>Show Blog</button>
      <button onClick={() => setSelectedComponent('course')}>Show Course</button>
      <button onClick={() => setSelectedComponent('none')}>Clear</button>

      <div style={{ marginTop: '20px' }}>
        <h4>ðŸ”¹ Conditional Rendering using if-else</h4>
        {renderComponent()}
      </div>

      <div style={{ marginTop: '20px' }}>
        <h4>ðŸ”¹ Conditional Rendering using element variable</h4>
        {element}
      </div>

      <div style={{ marginTop: '20px' }}>
        <h4>ðŸ”¹ Conditional Rendering using ternary</h4>
        {courseOrBook}
      </div>

      <div style={{ marginTop: '20px' }}>
        <h4>ðŸ”¹ Conditional Rendering using Logical AND</h4>
        {selectedComponent === 'blog' && <BlogDetails />}
      </div>

      <div style={{ marginTop: '20px' }}>
        <h4>ðŸ”¹ Rendering a List of All Components using map()</h4>
        {[<BookDetails key="b" />, <BlogDetails key="bl" />, <CourseDetails key="c" />]}
      </div>
    </div>
  );
}

export default App;
